require_once('workflows.php');
require_once('FileCache.php');

function load_data($api) {

    $opts = array(  
        'http'=>array(  
        'method'=>"GET",  
        'timeout'=>10
        )
    );  
    $context = stream_context_create($opts);       
    $obj = false;


    $fck  = md5('api2');
    $data = FileCache::get($fck);
        if(!$data)
        {
            $xml = @file_get_contents( $api,false, $context);
            $data = $xml;            
            FileCache::set($fck,$data,86400);
        }
    $obj=json_decode($data);

    return $obj;
}

$wf = new Workflows();

$orig = "{query}";


date_default_timezone_set('Asia/Shanghai');
if($orig){
    $obj = load_data('https://raw.github.com/hzlzh/AlfredWorkflow.com/master/workflow_api.json');

    $int = 1;
    if($obj != null):
        foreach( $obj as $key => $item ):
            if(!stristr($item -> workflow_name, $orig) && !stristr($item -> workflow_description_small, $orig) && !stristr($item -> workflow_author_name, $orig)) continue;

            $workflow_name = $item -> workflow_name;
            $workflow_version = $item -> workflow_version;
            $workflow_description_small = $item -> workflow_description_small;
            $workflow_type = $item -> workflow_type;
            $workflow_release_page = $item -> workflow_release_page;
            $workflow_type = $item -> workflow_type;
            $workflow_author_name = $item -> workflow_author_name;
            $workflow_author_site = $item -> workflow_author_site;
            $workflow_download_link = $item -> workflow_download_link;
            $workflow_file = $item -> workflow_file;
            $workflow_screenshot = $item -> workflow_screenshot;

            $wf->result( $workflow_name, $workflow_download_link, '[v'.$workflow_version.'] '.$workflow_name . ' — @'.$workflow_author_name,  $workflow_description_small, 'default.png'  );
            $int++; 
        endforeach;
    endif;
}

$results = $wf->results();
if ( count( $results ) == 0 ):
    $wf->result( 'alfredworkflow_500', 'Please make sure your internet works well.', 'No responce, try again!', 'Please make sure your internet works well.', 'icon.png' );
endif;

echo $wf->toxml();
